mapper.update:
update cart set num=#{num},
                addTime=#{addTime}
            where account=#{account} and id=#{id}

mapper.select:
select item.id, `name`,producer,birthday,marketPrice,price,description,put,img,`rank`,addTime,account, num
 from item join cart on item.id = cart.id
 where account=#{account} limit #{page},#{pageSize}
